import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput



flowFile = session.get()
if(!flowFile)return
def text = ''
def jsonSlurper = new JsonSlurper()



flowFile = session.write(flowFile, {inputStream, outputStream ->
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    def object = jsonSlurper.parseText(text)
    def data = []
    def batch = []
    def parms = []
    for (item in object.children) {
        if((item.name=="Data") && (item.attributes.Seq.toString())!="null"){
            def tmp1 = [(("Angle") + (item.attributes.Seq.toString())):item.attributes.Angle.toString()]
            data.add(tmp1)
            def tmp2 = [(("Torque") + (item.attributes.Seq.toString())):item.attributes.Torque.toString()]
            data.add(tmp2)
        }
        else if((item.name=="Data") && ((item.name=="Data") && (item.attributes.N.toString())!="DRIVERSIDE") && ((item.name=="Data") && (item.attributes.N.toString())!="VERSION") && ((item.name=="Data") && (item.attributes.N.toString())!="MasterLabel_DM") && ((item.name=="Data") && (item.attributes.N.toString())!="MasterLabel_DMC")){
                if((item.name=="Data") && (item.attributes.N.toString())!="EquipmentID"){
					def tmp = [(item.attributes.N.toString()):item.attributes.V.toString()]
					data.add(tmp)
					}
				if((item.name=="Data") && (item.attributes.N.toString())=="EquipmentID"){
					object.eqid = item.attributes.V.toString()
					}
            }
            else if(item.name=="Batch") {
                def tmp = item.attributes
                batch.add(tmp)
            }
				else if((item.name=="Parms") || ((item.name=="Data") && (item.attributes.N.toString())=="DRIVERSIDE") || ((item.name=="Data") && (item.attributes.N.toString())=="VERSION") || ((item.name=="Data") && (item.attributes.N.toString())=="MasterLabel_DM") || ((item.name=="Data") && (item.attributes.N.toString())=="MasterLabel_DMC")){
					def tmp = [(item.attributes.N.toString()):item.attributes.V.toString()]
					parms.add(tmp)
				}
    }
    object.data = JsonOutput.toJson(data)
    object.batch = JsonOutput.toJson(batch)
    object.parms = JsonOutput.toJson(parms)
    object.remove("children")
    object.remove("name")
    outputStream.write(JsonOutput.toJson(object).getBytes(StandardCharsets.UTF_8))
} as StreamCallback)




session.transfer(flowFile, REL_SUCCESS)

